import React, { useState } from "react";
import Navbar from "./components/navbar";
import Auth from "./components/auth";
import "semantic-ui-css/semantic.min.css";
import { UserSessionContext } from "./context/user-context";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import Home from "./components/home";
import "./App.css";

const GUEST_USER = { _guest: true, displayName: "Guest" };

function App() {
  const [user, setUser] = useState(GUEST_USER);

  function logIn(name) {
    setUser({ _guest: false, displayName: name });
  }

  function logOut(name) {
    setUser(GUEST_USER);
  }

  return (
    <Router>
      <div>
        <UserSessionContext.Provider value={{ user, logIn, logOut }}>
          <Navbar />
          <Auth />
        </UserSessionContext.Provider>
        <ul>
          <li>
            <Link to="/">Home</Link>
          </li>
          <li>
            <Link to="/profileNav">profileNav</Link>
          </li>
        </ul>
        <Switch>
          <Route exact path="/">
            <Home />
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
