import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import "./App.css";
import "semantic-ui-css/semantic.min.css";
import Home from "./components/home";
import Nav from "./components/nav";
import LogInForm from "./components/LogInForm";
import Project from "./components/project";
import SignUp from "./components/SignUp";

// const GUEST_USER = { _guest: true, displayName: "Guest" };

function App() {
  const [user, setUser] = useState({});
  const [form, setForm] = useState("");

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      fetch(`http://localhost:3001/auto_login`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      })
        .then(resp => resp.json())
        .then(data => {
          setUser(data);
          // console.log(data)
        });
    }
  }, []);

  const handleLogin = user => {
    setUser(user);
  };

  console.log(user);

  const handleFormSwitch = input => {
    setForm(input);
  };

  const handleAuthClick = () => {
    const token = localStorage.getItem("token");
    fetch(`http://localhost:3000/user_is_authed`, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
      .then(resp => resp.json())
      .then(data => console.log(data));
  };

  console.log(user);

  const renderForm = () => {
    switch (form) {
      case "login":
        return <LoginForm handleLogin={handleLogin} />;
        break;
      default:
        return <SignUp handleLogin={handleLogin} />;
    }
  };

  return (
    <Router>
      <div>
        {/* <UserSessionContext.Provider value={{ user, logIn, logOut }}> */}
        <Nav handleFormSwitch={handleFormSwitch} />
        {renderForm()}
        <button onClick={handleAuthClick} className="ui button">
          Access Authorized Route
        </button>
        <Switch>
          <Route path="/" exact component={Home} />
          <Route exact path="/signup" component={SignUp} />
          <Route path="/project" exact component={Project} />
          <Route
            path="/LogInForm"
            exact
            component={LogInForm}
            handleLogin={handleLogin}
          />
        </Switch>
        {/* </UserSessionContext.Provider> */}
      </div>
    </Router>
  );
}

export default App;
