import React, { useEffect, useContext } from "react";
import { UserContext } from "../context/UserContext";
import axios from "axios";
import useForm from "./useForm";
import { Form, Message } from "semantic-ui-react";

const SignUp = () => {
  const { values, handleChange, handleSubmit } = useForm(signUp);
  const [user, setUser] = useContext(UserContext);

  function signUp() {
    setUser(values);
    console.log(user);
  }

  useEffect(async () => {
    const api = `http://localhost:3001/api/v1/users`;
    await axios.post(api, { user }).then(res => {
      localStorage.setItem("user", JSON.stringify(res.data.user));
      localStorage.setItem("jwt", res.data.jwt);
      // setIsLoggedIn(true);
      // history.push('/');
    });
  }, []);

  return (
    <div>
      <Message
        attached
        header="Welcome to our site!"
        content="Fill out the form below to sign-up for a new account"
      />
      <Form className="attached fluid segment" onSubmit={handleSubmit}>
        <Form.Group widths="equal"></Form.Group>
        <Form.Group>
          <Form.Input
            placeholder="First Name"
            name="firstname"
            value={values.firstname}
            onChange={handleChange}
          />
          <Form.Input
            placeholder="Last Name"
            name="lastname"
            value={values.lastname}
            onChange={handleChange}
          />
        </Form.Group>
        <Form.Group>
          <Form.Input
            placeholder="Email"
            name="email"
            value={values.email}
            onChange={handleChange}
          />
          <Form.Input
            placeholder="Password"
            name="password"
            value={values.password}
            onChange={handleChange}
          />
        </Form.Group>
        <Form.Checkbox inline label="I agree to the terms and conditions" />
        <Form.Button color="teal" content="Submit" type="submit" />
      </Form>
    </div>
  );
};

export default SignUp;
