import React, { useState } from "react";
import useForm from "./useForm";
import { Form, Message } from "semantic-ui-react";

const SignUp = () => {
  const { values, handleChange, handleSubmit } = useForm(signUp);
  const [user, setUser] = useState({
    firstname: "",
    lastname: "",
    email: "",
    password: ""
  });

  function signUp(event) {
    const { name, value } = values;
    event.preventDefault();
    setUser(prevState => ({ ...prevState, [name]: value }));
    console.log(values);
    console.log(user);
    //set values to current user context to hold global variable
  }
  return (
    <div>
      <Message
        attached
        header="Welcome to our site!"
        content="Fill out the form below to sign-up for a new account"
      />
      <Form className="attached fluid segment" onSubmit={handleSubmit}>
        <Form.Group widths="equal"></Form.Group>
        <Form.Group>
          <Form.Input
            placeholder="First Name"
            name="firstname"
            value={values.firstname}
            onChange={handleChange}
          />
          <Form.Input
            placeholder="Last Name"
            name="lastname"
            value={values.lastname}
            onChange={handleChange}
          />
        </Form.Group>
        <Form.Group>
          <Form.Input
            placeholder="Email"
            name="email"
            value={values.email}
            onChange={handleChange}
          />
          <Form.Input
            placeholder="Password"
            name="password"
            value={values.password}
            onChange={handleChange}
          />
        </Form.Group>
        <Form.Checkbox inline label="I agree to the terms and conditions" />
        <Form.Button color="teal" content="Submit" type="submit" />
      </Form>
    </div>
  );
};

export default SignUp;
